mapper.delete:
delete from TM_DMP_RULE
where 
RULE_ID = #{ruleId, jdbcType=INTEGER}

mapper.update:
update TM_DMP_RULE
ST_ID =#{stId,jdbcType=INTEGER }, RS_ID =#{rsId,jdbcType=INTEGER }, RULE_NAME =#{ruleName,jdbcType=VARCHAR }, RULE_ENABLED =#{ruleEnabled,jdbcType=VARCHAR }, RULE_TYPE =#{ruleType,jdbcType=VARCHAR }, PRIORITY =#{priority,jdbcType=INTEGER }, REMARK =#{remark,jdbcType=VARCHAR }, RULE_OBJECT =#{ruleObject,jdbcType=VARCHAR }, UPDATE_DATE =#{updateDate,jdbcType=TIMESTAMP }, UPDATE_BY =#{updateBy,jdbcType=VARCHAR }, JPA_VERSION =#{jpaVersion,jdbcType=INTEGER },
where RULE_ID = #{ruleId, jdbcType=INTEGER}

mapper.select:
		select 		
 		from TM_DMP_RULE
		where 1=1 

			and ORG = #{org,jdbcType=VARCHAR }


			and ST_ID = #{stId,jdbcType=INTEGER }


			and RS_ID = #{rsId,jdbcType=INTEGER }


			and RULE_ID = #{ruleId,jdbcType=INTEGER }


			and RULE_NAME = #{ruleName,jdbcType=VARCHAR }


			and RULE_ENABLED = #{ruleEnabled,jdbcType=VARCHAR }


			and RULE_TYPE = #{ruleType,jdbcType=VARCHAR }


			and PRIORITY = #{priority,jdbcType=INTEGER }


			and REMARK = #{remark,jdbcType=VARCHAR }


			and RULE_OBJECT = #{ruleObject,jdbcType=VARCHAR }


			and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP }


			and CREATE_BY = #{createBy,jdbcType=VARCHAR }


			and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP }


			and UPDATE_BY = #{updateBy,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}

		select  ID 		
 		from TM_DMP_RULE
		where 1=1 

			and ORG = #{org,jdbcType=VARCHAR }


			and ST_ID = #{stId,jdbcType=INTEGER }


			and RS_ID = #{rsId,jdbcType=INTEGER }


			and RULE_ID = #{ruleId,jdbcType=INTEGER }


			and RULE_NAME = #{ruleName,jdbcType=VARCHAR }


			and RULE_ENABLED = #{ruleEnabled,jdbcType=VARCHAR }


			and RULE_TYPE = #{ruleType,jdbcType=VARCHAR }


			and PRIORITY = #{priority,jdbcType=INTEGER }


			and REMARK = #{remark,jdbcType=VARCHAR }


			and RULE_OBJECT = #{ruleObject,jdbcType=VARCHAR }


			and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP }


			and CREATE_BY = #{createBy,jdbcType=VARCHAR }


			and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP }


			and UPDATE_BY = #{updateBy,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}
