mapper.delete:
delete from TM_DMP_RULESET
where 
RS_ID = #{rsId, jdbcType=INTEGER}

mapper.update:
update TM_DMP_RULESET
ST_ID =#{stId,jdbcType=INTEGER }, RULE_SET_NAME =#{ruleSetName,jdbcType=VARCHAR }, RULE_SET_TYPE =#{ruleSetType,jdbcType=VARCHAR }, RULE_SET_ENABLED =#{ruleSetEnabled,jdbcType=VARCHAR }, IS_EXCLUSIVE =#{isExclusive,jdbcType=VARCHAR }, RULE_SET_OBJECT =#{ruleSetObject,jdbcType=CLOB }, REMARK =#{remark,jdbcType=VARCHAR }, UPDATE_DATE =#{updateDate,jdbcType=TIMESTAMP }, UPDATE_BY =#{updateBy,jdbcType=VARCHAR }, JPA_VERSION =#{jpaVersion,jdbcType=INTEGER },
where RS_ID = #{rsId, jdbcType=INTEGER}

mapper.select:
		select 		
 		from TM_DMP_RULESET
		where 1=1 

			and ORG = #{org,jdbcType=VARCHAR }


			and ST_ID = #{stId,jdbcType=INTEGER }


			and RS_ID = #{rsId,jdbcType=INTEGER }


			and RULE_SET_NAME = #{ruleSetName,jdbcType=VARCHAR }


			and RULE_SET_TYPE = #{ruleSetType,jdbcType=VARCHAR }


			and RULE_SET_ENABLED = #{ruleSetEnabled,jdbcType=VARCHAR }


			and IS_EXCLUSIVE = #{isExclusive,jdbcType=VARCHAR }


			and RULE_SET_OBJECT = #{ruleSetObject,jdbcType=CLOB }


			and REMARK = #{remark,jdbcType=VARCHAR }


			and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP }


			and CREATE_BY = #{createBy,jdbcType=VARCHAR }


			and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP }


			and UPDATE_BY = #{updateBy,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}

		select  ID 		
 		from TM_DMP_RULESET
		where 1=1 

			and ORG = #{org,jdbcType=VARCHAR }


			and ST_ID = #{stId,jdbcType=INTEGER }


			and RS_ID = #{rsId,jdbcType=INTEGER }


			and RULE_SET_NAME = #{ruleSetName,jdbcType=VARCHAR }


			and RULE_SET_TYPE = #{ruleSetType,jdbcType=VARCHAR }


			and RULE_SET_ENABLED = #{ruleSetEnabled,jdbcType=VARCHAR }


			and IS_EXCLUSIVE = #{isExclusive,jdbcType=VARCHAR }


			and RULE_SET_OBJECT = #{ruleSetObject,jdbcType=CLOB }


			and REMARK = #{remark,jdbcType=VARCHAR }


			and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP }


			and CREATE_BY = #{createBy,jdbcType=VARCHAR }


			and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP }


			and UPDATE_BY = #{updateBy,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}
