mapper.delete:
		delete from TM_DMP_STRATEGY_FUNCTION
		where 
		ST_CLASS = #{stClass, jdbcType=VARCHAR}
 	and 		FUN_CD = #{funCd, jdbcType=VARCHAR}

mapper.update:
update TM_DMP_STRATEGY_FUNCTION
FUN_NAME =#{funName,jdbcType=VARCHAR }, FUN_PARAM =#{funParam,jdbcType=VARCHAR }, DATA_TYPE =#{dataType,jdbcType=VARCHAR }, FUN_CONTENT =#{funContent,jdbcType=VARCHAR }, REMARK =#{remark,jdbcType=VARCHAR }, IF_USED =#{ifUsed,jdbcType=VARCHAR }, UPDATE_DATE =#{updateDate,jdbcType=TIMESTAMP }, UPDATE_BY =#{updateBy,jdbcType=VARCHAR }, JPA_VERSION=JPA_VERSION+1
where ST_CLASS = #{stClass, jdbcType=VARCHAR} and FUN_CD = #{funCd, jdbcType=VARCHAR} and JPA_VERSION = #{jpaVersion, jdbcType=INTEGER}

mapper.select:
		select			
		from TM_DMP_STRATEGY_FUNCTION
		where 
		ST_CLASS = #{stClass, jdbcType=VARCHAR}
 	and 		FUN_CD = #{funCd, jdbcType=VARCHAR}

		select 		
 		from TM_DMP_STRATEGY_FUNCTION
		where 1=1 

			and ORG = #{org,jdbcType=VARCHAR }


			and ST_CLASS = #{stClass,jdbcType=VARCHAR }


			and FUN_CD = #{funCd,jdbcType=VARCHAR }


			and FUN_NAME = #{funName,jdbcType=VARCHAR }


			and FUN_PARAM = #{funParam,jdbcType=VARCHAR }


			and DATA_TYPE = #{dataType,jdbcType=VARCHAR }


			and FUN_CONTENT = #{funContent,jdbcType=VARCHAR }


			and REMARK = #{remark,jdbcType=VARCHAR }


			and IF_USED = #{ifUsed,jdbcType=VARCHAR }


			and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP }


			and CREATE_BY = #{createBy,jdbcType=VARCHAR }


			and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP }


			and UPDATE_BY = #{updateBy,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}

		select  ID 		
 		from TM_DMP_STRATEGY_FUNCTION
		where 1=1 

			and ORG = #{org,jdbcType=VARCHAR }


			and ST_CLASS = #{stClass,jdbcType=VARCHAR }


			and FUN_CD = #{funCd,jdbcType=VARCHAR }


			and FUN_NAME = #{funName,jdbcType=VARCHAR }


			and FUN_PARAM = #{funParam,jdbcType=VARCHAR }


			and DATA_TYPE = #{dataType,jdbcType=VARCHAR }


			and FUN_CONTENT = #{funContent,jdbcType=VARCHAR }


			and REMARK = #{remark,jdbcType=VARCHAR }


			and IF_USED = #{ifUsed,jdbcType=VARCHAR }


			and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP }


			and CREATE_BY = #{createBy,jdbcType=VARCHAR }


			and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP }


			and UPDATE_BY = #{updateBy,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}
