mapper.delete:
		delete from TM_DMP_STRATEGY_VAR
		where 
		ST_CLASS = #{stClass, jdbcType=VARCHAR}
 	and 		VAR_TYPE = #{varType, jdbcType=VARCHAR}
 	and 		VAR_CD = #{varCd, jdbcType=VARCHAR}

mapper.update:
update TM_DMP_STRATEGY_VAR
VAR_NAME =#{varName,jdbcType=VARCHAR }, IS_INPUT =#{isInput,jdbcType=VARCHAR }, IS_OUTPUT =#{isOutput,jdbcType=VARCHAR }, REMARK =#{remark,jdbcType=VARCHAR }, IF_USED =#{ifUsed,jdbcType=VARCHAR }, UPDATE_DATE =#{updateDate,jdbcType=TIMESTAMP }, UPDATE_BY =#{updateBy,jdbcType=VARCHAR }, JPA_VERSION=JPA_VERSION+1
where ST_CLASS = #{stClass, jdbcType=VARCHAR} and VAR_TYPE = #{varType, jdbcType=VARCHAR} and VAR_CD = #{varCd, jdbcType=VARCHAR} and JPA_VERSION = #{jpaVersion, jdbcType=INTEGER}

mapper.select:
		select			
		from TM_DMP_STRATEGY_VAR
		where 
		ST_CLASS = #{stClass, jdbcType=VARCHAR}
 	and 		VAR_TYPE = #{varType, jdbcType=VARCHAR}
 	and 		VAR_CD = #{varCd, jdbcType=VARCHAR}

		select 		
 		from TM_DMP_STRATEGY_VAR
		where 1=1 

			and ORG = #{org,jdbcType=VARCHAR }


			and ST_CLASS = #{stClass,jdbcType=VARCHAR }


			and VAR_TYPE = #{varType,jdbcType=VARCHAR }


			and VAR_CD = #{varCd,jdbcType=VARCHAR }


			and VAR_NAME = #{varName,jdbcType=VARCHAR }


			and IS_INPUT = #{isInput,jdbcType=VARCHAR }


			and IS_OUTPUT = #{isOutput,jdbcType=VARCHAR }


			and REMARK = #{remark,jdbcType=VARCHAR }


			and IF_USED = #{ifUsed,jdbcType=VARCHAR }


			and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP }


			and CREATE_BY = #{createBy,jdbcType=VARCHAR }


			and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP }


			and UPDATE_BY = #{updateBy,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}

		select  ID 		
 		from TM_DMP_STRATEGY_VAR
		where 1=1 

			and ORG = #{org,jdbcType=VARCHAR }


			and ST_CLASS = #{stClass,jdbcType=VARCHAR }


			and VAR_TYPE = #{varType,jdbcType=VARCHAR }


			and VAR_CD = #{varCd,jdbcType=VARCHAR }


			and VAR_NAME = #{varName,jdbcType=VARCHAR }


			and IS_INPUT = #{isInput,jdbcType=VARCHAR }


			and IS_OUTPUT = #{isOutput,jdbcType=VARCHAR }


			and REMARK = #{remark,jdbcType=VARCHAR }


			and IF_USED = #{ifUsed,jdbcType=VARCHAR }


			and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP }


			and CREATE_BY = #{createBy,jdbcType=VARCHAR }


			and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP }


			and UPDATE_BY = #{updateBy,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}
