		 #{lineNo,jdbcType=INTEGER } , #{content,jdbcType=VARCHAR } , #{startBpmn,jdbcType=VARCHAR } , #{failReason,jdbcType=VARCHAR } ,
		 #{batchDate,jdbcType=TIMESTAMP } , #{jpaVersion,jdbcType=INTEGER } 
