mapper.delete:
delete from TM_TASK_TRANSFER
where 
ID = #{id, jdbcType=INTEGER}

mapper.update:
update TM_TASK_TRANSFER
APP_NO =#{appNo,jdbcType=VARCHAR }, TRANSFER_TYPE =#{transferType,jdbcType=VARCHAR }, TASK_KEY =#{taskKey,jdbcType=VARCHAR }, TASK_NAME =#{taskName,jdbcType=VARCHAR }, RTF_STATE =#{rtfState,jdbcType=VARCHAR }, OWNER =#{owner,jdbcType=VARCHAR }, ASSIGNER =#{assigner,jdbcType=VARCHAR }, CLAIM_TIME =#{claimTime,jdbcType=TIMESTAMP }, END_TIME =#{endTime,jdbcType=TIMESTAMP }, STATUS =#{status,jdbcType=VARCHAR }, JPA_VERSION =#{jpaVersion,jdbcType=INTEGER },
where ID = #{id, jdbcType=INTEGER}

mapper.select:
		select 		
 		from TM_TASK_TRANSFER
		where 1=1 

			and ID = #{id,jdbcType=INTEGER }


			and ORG = #{org,jdbcType=VARCHAR }


			and APP_NO = #{appNo,jdbcType=VARCHAR }


			and TRANSFER_TYPE = #{transferType,jdbcType=VARCHAR }


			and TASK_KEY = #{taskKey,jdbcType=VARCHAR }


			and TASK_NAME = #{taskName,jdbcType=VARCHAR }


			and RTF_STATE = #{rtfState,jdbcType=VARCHAR }


			and OWNER = #{owner,jdbcType=VARCHAR }


			and ASSIGNER = #{assigner,jdbcType=VARCHAR }


			and CLAIM_TIME = #{claimTime,jdbcType=TIMESTAMP }


			and END_TIME = #{endTime,jdbcType=TIMESTAMP }


			and STATUS = #{status,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}

		select  ID 		
 		from TM_TASK_TRANSFER
		where 1=1 

			and ID = #{id,jdbcType=INTEGER }


			and ORG = #{org,jdbcType=VARCHAR }


			and APP_NO = #{appNo,jdbcType=VARCHAR }


			and TRANSFER_TYPE = #{transferType,jdbcType=VARCHAR }


			and TASK_KEY = #{taskKey,jdbcType=VARCHAR }


			and TASK_NAME = #{taskName,jdbcType=VARCHAR }


			and RTF_STATE = #{rtfState,jdbcType=VARCHAR }


			and OWNER = #{owner,jdbcType=VARCHAR }


			and ASSIGNER = #{assigner,jdbcType=VARCHAR }


			and CLAIM_TIME = #{claimTime,jdbcType=TIMESTAMP }


			and END_TIME = #{endTime,jdbcType=TIMESTAMP }


			and STATUS = #{status,jdbcType=VARCHAR }


			and JPA_VERSION = #{jpaVersion,jdbcType=INTEGER }




				order by ${_SORT_NAME} ${_SORT_ORDER}
